module.exports = (sequelize, DataTypes) => {
    // 表定义
    const App = sequelize.define('app', {
        id: {
            type: DataTypes.INTEGER,
            primaryKey: true,
            autoIncrement: true
        },
        name: {
            type: DataTypes.STRING
        },
        thumb: {
            type: DataTypes.STRING
        },
        description: {
            type: DataTypes.STRING
        },
        status: {
            type: DataTypes.INTEGER,
        },
        createdAt: {
            type: DataTypes.TIME,
            field: 'created_at'
        },
        updatedAt: {
            type: DataTypes.TIME,
            field: 'updated_at'
        },
        uid: {
            type: DataTypes.INTEGER
        },
    });

    // 关联关系定义
    App.associate = function (sequelize) {
        const User = sequelize.User;
        this.belongsTo(User, { foreignKey: 'uid' });
    }
    return App;
}
